mapper.insert:
INSERT INTO MAPINFO(mnum, kakaoemail, mtitle, mlocal, mcontent, beginlat, beginlng) 
VALUES (
	mapinfo_seq.nextval, #{kakaoEmail}, #{mtitle}, #{mlocal}, #{mcontent}, #{beginLat}, #{beginLng}
)

mapper.update:
UPDATE MAPINFO 
SET mtitle = #{mtitle}, mlocal = #{mlocal}, mcontent = #{mcontent}, beginlat = #{beginLat}, beginlng = #{beginLng} 
WHERE mnum = #{mnum}
